from django.db import modelsclass Configuration(models.Model):    id = models.AutoField(primary_key=True)    name = models.CharField(max_length=50)class Object(models.Model):    config = models.ForeignKey("Configuration")    view = models.ForeignKey('View', blank=True, null=True, on_delete=models.CASCADE, default=None)    id = models.AutoField(primary_key=True)    oid = models.CommaSeparatedIntegerField(max_length=30)    x = models.FloatField()    y = models.FloatField()    color = models.CharField(max_length=30)    shape = models.CharField(max_length=30)    size = models.IntegerField()    image = models.URLField(max_length=1024)    scale = models.FloatField(default=0)    class Meta:        unique_together = (('oid', 'config', 'view'))class ObjectClass(models.Model):    id = models.AutoField(primary_key=True)    cid = models.IntegerField()    config = models.ForeignKey("Configuration")    view = models.ForeignKey('View', blank=True, null=True, on_delete=models.CASCADE, default=None)    x = models.IntegerField(default=0)    y = models.IntegerField(default=0)    color = models.CharField(max_length=30,default="black")    shape = models.CharField(max_length=30,default="circle")    size = models.IntegerField(default=1)    image = models.URLField(max_length=1024)    scale = models.FloatField(default=0)    class Meta:        unique_together = ('cid', 'config', 'view')class Background(models.Model):    id = models.AutoField(primary_key=True)    config = models.ForeignKey("Configuration")    view = models.ForeignKey('View', blank=True, null=True, on_delete=models.CASCADE, default=None)    x = models.IntegerField(default=0)    y = models.IntegerField(default=0)    width = models.IntegerField(default=50)    height = models.IntegerField(default=50)    image = models.URLField(max_length=1024)    z = models.FloatField(default=0)    class Meta:        unique_together = ('id', 'config', 'view')class View(models.Model):    id = models.AutoField(primary_key=True)    config = models.ForeignKey("Configuration")    name = models.CharField(max_length=100,default="New view")    image = models.URLField(max_length=1024)